import React, { useState } from "react";
import axios from "axios";
import DietForm from "./DietForm";
import "./Diet.css";

const DietApp = () => {
  const [dietData, setDietData] = useState([]);
  const [bmi, setBMI] = useState(0);
  const [caloricIntake, setCaloricIntake] = useState(0);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    gender: "Male",
    unit: "metric",
    height: 180,
    weight: 75,
    primaryPurpose: "Lose Weight",
    mealsPerDay: 3,
    snacksPerDay: 2,
    dietPreference: "Vegetarian",
    allergy: "None"
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    // Convert height to cm and weight to kg if using imperial units
    let height = parseFloat(formData.height);
    let weight = parseFloat(formData.weight);
    if (formData.unit === "imperial") {
      height =
        parseFloat(formData.heightFeet || 0) * 30.48 +
        parseFloat(formData.heightInches || 0) * 2.54;
      weight = parseFloat(formData.weightPounds) / 2.20462;
    }

    const convertedData = {
      ...formData,
      height: height,
      weight: weight,
      mealsPerDay: parseInt(formData.mealsPerDay, 10),
      snacksPerDay: parseInt(formData.snacksPerDay, 10),
    };

    console.log("Form data:", convertedData);
    try {
      const response = await axios.post(
        "http://localhost:8080/get-diet-data",
        convertedData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      console.log("Response data:", response.data);
      setDietData(response.data.dietPlan);
      setBMI(response.data.bmi);
      setCaloricIntake(response.data.calories);
    } catch (error) {
      console.error("Error fetching diet data:", error);
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="App">
      <h1>Diet Plan</h1>
      <DietForm
        formData={formData}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        loading={loading}
      />
      <div className="results">
        {bmi !== 0 && <p className="result-label">BMI: {bmi.toFixed(2)}</p>}
        {caloricIntake !== 0 && (
          <p className="result-label">
            Caloric Intake: {caloricIntake.toFixed(2)} kcal/day
          </p>
        )}
      </div>
      {dietData.length > 0 && (
        <div className="diet-plan">
          <h2>Your Diet Plan</h2>
          <ul>
            {dietData.map((item, index) => (
              <li key={index}>{item}</li>
            ))}
          </ul>
        </div>
      )}
    </div>
  );
};

export default DietApp;
