import React, { useState } from "react";
import axios from "axios";
import DietForm from "./DietForm";
import DietTable from "./DietTable";
import "./Diet.css";

const DietApp = () => {
  const [meals, setMeals] = useState([]);
  const [loading, setLoading] = useState(false);
  const [formData, setFormData] = useState({
    gender: "Male",
    unit: "metric",
    height: 180,
    weight: 75,
    primaryPurpose: "Lose Weight",
    mealsPerDay: 3,
    snacksPerDay: 2,
    dietPreference: "Vegetarian",
    allergies: "None",
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = async (event) => {
    event.preventDefault();
    setLoading(true);

    // Convert height and weight to metric if necessary
    let height = formData.unit === "imperial" 
      ? formData.heightFeet * 30.48 + formData.heightInches * 2.54 
      : formData.height;
    let weight = formData.unit === "imperial" 
      ? formData.weightPounds / 2.20462 
      : formData.weight;

    const convertedData = {
      ...formData,
      height,
      weight,
    };

    try {
      const response = await axios.post(
        "http://localhost:8080/get-meal-data",
        convertedData,
        {
          headers: {
            "Content-Type": "application/json",
          },
        }
      );
      setMeals(response.data.meals);
    } catch (error) {
      console.error("Error fetching meal data:", error);
    } finally {
      setLoading(false);
    }
  };

  const updateMeal = (day, oldMealType, newMeal) => {
    setMeals((prevMeals) =>
      prevMeals.map((meal) =>
        meal.day === day && meal.mealType === oldMealType ? newMeal : meal
      )
    );
  };

  return (
    <div className="App">
      <h1>Diet Plan</h1>
      <DietForm
        formData={formData}
        handleChange={handleChange}
        handleSubmit={handleSubmit}
        loading={loading}
      />
      {meals.length > 0 && (
        <DietTable
          meals={meals}
          formData={formData}
          updateMeal={updateMeal}
        />
      )}
    </div>
  );
};

export default DietApp;
