import React from "react";
import "./Diet.css";
import axios from "axios";

const DietTable = ({ meals, formData, updateMeal }) => {
  const groupByDay = (meals) => {
    return meals.reduce((acc, meal) => {
      if (!acc[meal.day]) {
        acc[meal.day] = [];
      }
      acc[meal.day].push(meal);
      return acc;
    }, {});
  };

  const handleSwap = async (meal) => {
    try {
      const response = await axios.post("http://localhost:8080/swap-meal", meal);
      const newMeal = response.data[0];
      updateMeal(meal.day, meal.mealType, newMeal);
    } catch (error) {
      console.error("Error swapping meal:", error);
    }
  };

  const groupedMeals = groupByDay(meals);

  return (
    <div className="meal-table-container">
      <table className="meal-table">
        <thead>
          <tr>
            <th>Meal Type</th>
            <th>Ingredients</th>
            <th>Instructions</th>
            <th>Nutrition</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(groupedMeals).map((day, index) => (
            <React.Fragment key={index}>
              <tr className="day-header">
                <td colSpan="5">{day}</td>
              </tr>
              {groupedMeals[day].map((meal, idx) => (
                <tr key={idx}>
                  <td>{meal.mealType}</td>
                  <td>
                    {meal.ingredients.map((ingredient, i) => (
                      <div key={i}>{ingredient}</div>
                    ))}
                  </td>
                  <td>{meal.instructions}</td>
                  <td>
                    {meal.nutrition &&
                      Object.keys(meal.nutrition).map((key, i) => (
                        <div key={i}>
                          {key}: {meal.nutrition[key]}
                        </div>
                      ))}
                  </td>
                  <td>
                    <button onClick={() => handleSwap(meal)}>Swap</button>
                  </td>
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DietTable;
