import React from "react";
import "./Diet.css";
import axios from "axios";

const DietTable = ({ dietPlan, formData, updateDiet }) => {
  const groupByDay = (dietPlan) => {
    return dietPlan.reduce((acc, meal) => {
      if (!acc[meal.day]) {
        acc[meal.day] = [];
      }
      acc[meal.day].push(meal);
      return acc;
    }, {});
  };

  const handleSwap = async (meal) => {
    const otherMeals = dietPlan
      .filter((m) => m.day === meal.day && m.mealType !== meal.mealType)
      .map((m) => m.mealType);

    try {
      const response = await axios.post("http://localhost:8080/swap-meal", {
        meal: meal,
        otherMeals: otherMeals,
        dietPreference: formData.dietPreference,
        allergy: formData.allergy,
      });
      console.log("Swap response data:", response.data); // Log the response data
      const oldMealType = response.data.oldMealType;
      const newMeal = response.data.newMeal;
      updateDiet(meal.day, oldMealType, newMeal);
    } catch (error) {
      console.error("Error swapping meal:", error);
    }
  };

  const groupedMeals = groupByDay(dietPlan);

  return (
    <div className="diet-table-container">
      <table className="diet-table">
        <thead>
          <tr>
            <th>Day</th>
            <th>Meal Type</th>
            <th>Ingredients</th>
            <th>Instruction</th>
            <th>Nutrition</th>
            <th>Action</th>
          </tr>
        </thead>
        <tbody>
          {Object.keys(groupedMeals).map((day, index) => (
            <React.Fragment key={index}>
              <tr className="day-header">
                <td colSpan="6">{day}</td>
              </tr>
              {groupedMeals[day].map((meal, idx) => (
                <tr key={idx}>
                  <td>{meal.mealType}</td>
                  <td>{meal.ingredients.join(", ")}</td>
                  <td>{meal.instruction}</td>
                  <td>{meal.nutrition}</td>
                  <td>
                    <button
                      className="swap-button"
                      onClick={() => handleSwap(meal)}
                    >
                      Swap
                    </button>
                  </td>
                </tr>
              ))}
            </React.Fragment>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default DietTable;
